// Footer.js
import styles from '../styles/Footer.module.sass'
import { Waypoint } from 'react-waypoint';


export default function Footer({ home=false }) {
    return (
        <footer className={home ? styles.footer:styles.footerPost}>
            {home && <Waypoint onEnter={({ previousPosition, currentPosition, event }) => {ChangeBackground('#D4E1FB');}} topOffset='200px' bottomOffset='200px'/>}
            {home && <h2 className={styles.heading2}>My profile</h2>}
            <p className={styles.description}>I'm a UX/UI designer, but I studied cognitive science. I love design, human-computer interaction, philosophy, open-source and some more. I'm also a firm believer in social change and would like to leave the world having made it at least a little bit better.</p>
            <img 
                className={styles.profileImage}
                src="/images/profile.jpg" 
                alt="Me standing amongst people at a protest. I have a mask on (coronavirus)."/>
        </footer>

    )
}
